// Config
let debounceTime = Number(env.get("DebounceTime"));
if (isNaN(debounceTime) || debounceTime <= 0) {
    debounceTime = 3000;  // default to 3000ms if not set or invalid
}
const updateInterval = 50; // ms for countdown updates

// Get local state
let timeout = context.get('timeout');
let interval = context.get('interval');
let lastValue = msg.payload;
let startTime = Date.now();

// Clear existing timers
if (timeout) clearTimeout(timeout);
if (interval) clearInterval(interval);

// Set new timeout
timeout = setTimeout(() => {
    node.send({ payload: lastValue });
    clearInterval(interval);
    context.set('timeout', null);
    context.set('interval', null);
    node.status({ fill: "green", shape: "dot", text: "Sent" });
}, debounceTime);

// Start countdown status updates
interval = setInterval(() => {
    let remaining = debounceTime - (Date.now() - startTime);
    if (remaining < 0) remaining = 0;
    node.status({ fill: "yellow", shape: "ring", text: remaining + "ms" });
}, updateInterval);

// Store new state
context.set('timeout', timeout);
context.set('interval', interval);
context.set('lastValue', lastValue);
context.set('startTime', startTime);

// Do not send anything immediately
return null;

var date = new Date(msg.payload.ts);
//msg.payload = date;
msg.payload = date.toLocaleString('de-DE', { hour12:false });
return msg;

